import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { Loading } from '../components';
import * as movieAPI from '../services/movieAPI';

class MovieDetails extends Component {
  constructor(props) {
    super(props);
    this.state = {
      movie: {},
      loading: true,
    };
    this.fetchMovieApi = this.fetchMovieApi.bind(this);
  }

  componentDidMount() {
    this.fetchMovieApi();
  }

  fetchMovieApi = async () => {
    const { match } = this.props;
    const { id } = match.params;
    const movie = await movieAPI.getMovie(id);
    this.setState({
      movie, loading: false,
    });
  };

  render() {
    const { movie, loading } = this.state;
    const { id, title, storyline, imagePath, genre, rating, subtitle } = movie;
    const { deleteMovie } = movieAPI;
    return (
      <div className="details-page" data-testid="movie-details">
        { loading ? <Loading /> : (
          <>
            <img alt="Movie Cover" src={ `../${imagePath}` } />
            <p>{ `Title: ${title}` }</p>
            <p>{ `Subtitle: ${subtitle}` }</p>
            <p className="details-storyline">{ `Storyline: ${storyline}` }</p>
            <p>{ `Genre: ${genre}` }</p>
            <p>{ `Rating: ${rating}` }</p>
            <div>
              <Link className="link" to={ `/movies/${id}/edit` }>EDITAR</Link>
              <Link className="" to="/">VOLTAR</Link>
              <Link className="" to="/" onClick={ () => deleteMovie(id) }>DELETAR</Link>
            </div>
          </>
        ) }
      </div>
    );
  }
}
export default MovieDetails;
MovieDetails.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};
